// Import necessary modules
const React = require('react');
const { useState, useEffect } = require('react');
const { Calendar, momentLocalizer } = require('react-big-calendar');
const moment = require('moment');
require('react-big-calendar/lib/css/react-big-calendar.css');
// Import the styles for the calendar


// Create a localizer using moment
const localizer = momentLocalizer(moment);

// Define the EventList functional component
const EventList = () => {
  // State to store the events
  const [events, setEvents] = useState([]);

  // Fetch events from the API when the component mounts
  useEffect(() => {
    fetch('/api/events')
      .then((response) => response.json())
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  }, []);

  // Render the component
  return (
    <div>
      <h2>Event Calendar</h2>
      {/* Render the Calendar component with events */}
      <Calendar
        localizer={localizer}
        events={events.map((event) => ({
          id: event.id,
          title: event.event_name,
          start: new Date(event.event_date),
          end: new Date(event.event_date),
        }))}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
      />
    </div>
  );
};

// Export the EventList component
export default EventList;
